sp_widget.client_script:
function($rootScope, $scope) {
  /* widget controller */
	var c = this;
	var selectedNote;
	c.selectItem = function(idx) {
		selectedNote = c.data.notes[idx];
		selectedNoteId = c.data.notes[idx].sys_id;
		console.log('Note ID ' + selectedNoteId);
		$rootScope.noteID = selectedNoteId;
		$rootScope.$emit('selectNote', selectedNoteId);
		return selectedNote;
	}

	// Update a note on the list after the body has been updated. Update only on client-side since the body client script already sends an update to the server.
	$rootScope.$on('update', function(event, data) {
		// Store the index of the selected note for updating it
		var noteIndex = c.data.notes.indexOf(selectedNote);
		// Shorten the note and update
		c.data.notes[noteIndex].title = $rootScope.note.title;
		c.data.notes[noteIndex].note = $rootScope.note.note.slice(0, 20);
	});	
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while (noteGR.next()) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number, title, sys_id');
		noteObj.note = noteGR.getValue('note').slice(0, 20);
		data.notes.push(noteObj);
	}

})();
